import React, { useState } from 'react';
import '../styling/TripPreferences.css'; // Make sure to have this CSS file
import { DemoItem } from '@mui/x-date-pickers/internals/demo';
import { MultiSectionDigitalClock } from '@mui/x-date-pickers';
import { DateTimePicker } from '@mui/x-date-pickers/DateTimePicker';
const TripPreferences = () => {
  const [showClock, setShowClock] = useState(false);

  const handleToggleClock = () => {
    setShowClock(!showClock);
  };

  return (
    <div className={`trip-preferences ${showClock ? 'expanded' : ''}`}>
      <h2>Trip Preferences</h2>
      <p>Tell us your preferences to personalize your ideal road trip route!</p>
      <div className="time-inputs">
        <button className="time-input" onClick={handleToggleClock}>
          <label>Departure Time</label>
          <div className="time">Sat, Jan 27 3:15 PM</div>
        </button>
        <button className="time-input">
          <label>Target Arrival Time</label>
          <div className="time">Sat, Jan 27 3:15 PM</div>
        </button>
      </div>
      {showClock && (
        <DemoItem label={'"hours", "minutes" and "seconds"'}>
          <MultiSectionDigitalClock views={['hours', 'minutes', 'seconds']} />
        </DemoItem>
      )}
      <button className="next-button">Next</button>
    </div>
  );
};

export default TripPreferences;
